mapper.select:
select t3.id as project_id,t1.parent_domain, t1.domain, t1.company,INET_NTOA(t1.ip_long) as ip, t1.is_domain, t1.is_major from scan_host t1 left join scan_project_host t2 on t2.`host` = t1.domain and t2.del_flg = 0 left join scan_project t3 on t3.id = t2.project_id and t3.del_flg = 0 where t1.del_flg = 0
and t3.user_id = #{userId}
GROUP BY t1.domain

select distinct t1.parent_domain, t2.scan_ports from scan_host t1
join host_company t2 on t2.host = t1.parent_domain


        t2.company IN

            #{company}



        t1.parent_domain IN

            #{parentDomain}



        t1.domain IN

            #{domain}

mapper.delete:
delete scan_host from scan_host where scan_host.is_scanning = 0
AND scan_host.${field} in
#{tagValue}

mapper.update:
update scan_host
set is_scanning = 0,scan_ports = #{scanPorts}
where ip_long = #{ipLong}

mapper.select:
select distinct company
from scan_host
where 1=1

    and company not in

        #{company}


limit #{limit}
