mapper.select:
select t1.*, INET_NTOA(t1.ip_long) as ip, t2.domain, t2.parent_domain, t2.company, t4.name as project_name, t5.url, t5.title, t5.cms, u.user_id, u.user_name from scan_port t1 join scan_host t2 on t2.ip_long = t1.ip_long and t2.del_flg = 0 LEFT join scan_project_host t3 on t3.`host` = t2.domain and t3.del_flg = 0 LEFT join scan_project t4 on t4.id = t3.project_id and t4.del_flg = 0 left join scan_host_port t5 on t5.domain = t2.domain and t5.`port` = t1.`port` and t5.del_flg = 0 LEFT join sys_user u on u.user_id = t4.user_id and u.del_flg = 0 where t1.del_flg = 0
and t4.id = #{params.projectId}
and t4.user_id = #{params.userId}
and t2.company like concat('%', #{params.company}, '%')
and t2.parent_domain like concat('%', #{params.parentDomain}, '%')
and t2.domain like concat('%', #{params.domain}, '%')
and t1.ip_long = #{params.ipLong}
and t1.port like concat('%', #{params.port}, '%')
and t1.server_name like concat('%', #{params.serverName}, '%')

select DISTINCT t1.*, INET_NTOA(t1.ip_long) as ip, t2.domain, t2.parent_domain, t2.company, t4.name as project_name, t5.url, t5.title, t5.cms, u.user_id, u.user_name from scan_port t1 join scan_host t2 on t2.ip_long = t1.ip_long and t2.del_flg = 0 join scan_project_host t3 on t3.`host` = t2.domain and t3.del_flg = 0 join scan_project t4 on t4.id = t3.project_id and t4.del_flg = 0 left join scan_host_port t5 on t5.domain = t2.domain and t5.`port` = t1.`port` and t5.del_flg = 0 LEFT join sys_user u on u.user_id = t4.user_id and u.del_flg = 0 where t1.del_flg = 0
and t4.id = #{params.projectId}
and t4.user_id = #{params.userId}
and t2.company like concat('%', #{params.company}, '%')
and t2.parent_domain like concat('%', #{params.parentDomain}, '%')
and t2.domain like concat('%', #{params.domain}, '%')
and t1.ip_long = #{params.ipLong}
and t1.port like concat('%', #{params.port}, '%')
and t1.server_name like concat('%', #{params.serverName}, '%')
order by t2.parent_domain, t2.domain, t1.ip_long, t1.port

mapper.update:
update scan_port set del_flg = 1
where ip = #{ip}
and port = #{port}

mapper.delete:
delete scan_port from scan_host,scan_port where scan_host.ip_long = scan_port.ip_long and scan_host.is_scanning = 0
AND scan_host.${field} in
#{tagValue}
