mapper.select:
select distinct rm.menu_id from sys_user_role ur
LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id
where ur.user_id = #{userId}

SELECT u.*, ( SELECT GROUP_CONCAT( DISTINCT r.role_name ) FROM sys_user_role ur LEFT JOIN sys_role r ON ur.role_id = r.role_id WHERE ur.user_id = u.user_id AND r.del_flg = 0 ) AS roleNameArr, cu.NAME AS unit_name FROM sys_user u LEFT JOIN cm_unit cu ON cu.unit_id = u.unit_id AND cu.del_flg = 0 WHERE u.del_flg = 0
and u.user_name like concat('%',#{params.userName},'%')
and u.account_type = #{params.accountType}
and u.unit_id = #{params.unitId}
order by cu.unit_code

mapper.delete:
delete from sys_user where user_id in

    #{userId}

;
delete from sys_user_role where user_id in

    #{userId}

mapper.select:
SELECT u.*, ( SELECT GROUP_CONCAT( DISTINCT r.role_name ) FROM sys_user_role ur LEFT JOIN sys_role r ON ur.role_id = r.role_id WHERE ur.user_id = u.user_id AND r.del_flg = 0 ) AS roleNameArr, cu.NAME AS unit_name FROM sys_user u LEFT JOIN cm_unit cu ON cu.unit_id = u.unit_id AND cu.del_flg = 0 WHERE u.del_flg = 0
and u.user_name like concat('%',#{params.userName},'%')
and u.account_type = #{params.accountType}
and u.unit_id = #{params.unitId}
and u.user_id != #{params.userId}
and u.mobile = #{params.mobile}
order by u.unit_id
